import {Component} from "react";
import React from "react";

class CTAForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            message: ''
        };
    }

    handleSubmit = (event) => {
        event.preventDefault()
    };

    handleChange = (event) => {
        this.setState({value: event.target.value});
    };

    render() {
        return (
            <div className="CTAForm">
               <Form onSubmit={this.handleSubmit}>
                   <input type="text" value={this.state.name} onChange={this.handleChange} placeholder="Your Name"/>
                   <input type="text" value={this.state.email} onChange={this.handleChange} placeholder="Email"/>
                   <input type="text" value={this.state.message} onChange={this.handleChange} placeholder="Message"/>
                   <input type="submit" value="Submit" />
               </Form>
            </div>
        );
    }
}

export default CTAForm;
